import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Navbar, Nav, NavItem, NavLink, Dropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';
import './AdminPanel.css'; // Import the CSS file

const AdminPanel = ({ setAuthToken, user }) => {
  const navigate = useNavigate();
  const [isOpen, setIsOpen] = useState(true); // Set initial state to true to open the sidebar by default
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  const handleLogout = () => {
    sessionStorage.removeItem('authToken');
    setAuthToken(null);
    navigate('/login');
  };

  const handleViewProfile = () => {
    navigate('/profile');
  };

  const getUserInitial = () => {
    return user && user.name ? user.name.charAt(0).toUpperCase() : '';
  };

  return (
    <div className="d-flex" id="wrapper">
      <div className={`bg-light border-right ${isOpen ? 'toggled' : ''}`} id="sidebar-wrapper">
        <div className="sidebar-heading">Admin Panel</div>
        <div className="list-group list-group-flush">
          <NavLink href="#" className="list-group-item list-group-item-action bg-light">Dashboard</NavLink>
          <NavLink href="#" className="list-group-item list-group-item-action bg-light">Projects</NavLink>
          <NavLink href="#" className="list-group-item list-group-item-action bg-light">Forms</NavLink>
          <NavLink href="#" className="list-group-item list-group-item-action bg-light">Submissions</NavLink>
        </div>
      </div>
      <div id="page-content-wrapper">
        <Navbar color="light" light className="border-bottom">
          <div className="user-avatar" onClick={toggleSidebar}>
            {getUserInitial()}
          </div>
          <Nav className="ms-auto" navbar>
            <NavItem>
              <Dropdown isOpen={dropdownOpen} toggle={toggleDropdown}>
                <DropdownToggle caret className="user-avatar">
                  {getUserInitial()}
                </DropdownToggle>
                <DropdownMenu right>
                  <DropdownItem onClick={handleViewProfile}>View Profile</DropdownItem>
                  <DropdownItem divider />
                  <DropdownItem onClick={handleLogout}>Logout</DropdownItem>
                </DropdownMenu>
              </Dropdown>
            </NavItem>
          </Nav>
        </Navbar>
        <div className="container-fluid">
          <h2>Admin Panel</h2>
          {/* Add your admin panel content here */}
        </div>
      </div>
    </div>
  );
};

export default AdminPanel;
