import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button, Navbar, Nav, NavItem, NavLink } from 'reactstrap';
import './AdminPanel.css'; // Import the CSS file

const AdminPanel = ({ setAuthToken }) => {
  const navigate = useNavigate();
  const [isOpen, setIsOpen] = useState(false);

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  const handleLogout = () => {
    sessionStorage.removeItem('authToken');
    setAuthToken(null);
    navigate('/login');
  };

  return (
    <div className="d-flex" id="wrapper">
      <div className={`bg-light border-right ${isOpen ? 'toggled' : ''}`} id="sidebar-wrapper">
        <div className="sidebar-heading">Admin Panel</div>
        <div className="list-group list-group-flush">
          <NavLink href="#" className="list-group-item list-group-item-action bg-light">Dashboard</NavLink>
          <NavLink href="#" className="list-group-item list-group-item-action bg-light">Projects</NavLink>
          <NavLink href="#" className="list-group-item list-group-item-action bg-light">Forms</NavLink>
          <NavLink href="#" className="list-group-item list-group-item-action bg-light">Submissions</NavLink>
        </div>
      </div>
      <div id="page-content-wrapper">
        <Navbar color="light" light className="border-bottom">
          <Button color="primary" onClick={toggleSidebar}>Toggle Sidebar</Button>
          <Nav className="ms-auto" navbar>
            <NavItem>
              <Button color="danger" onClick={handleLogout}>Logout</Button>
            </NavItem>
          </Nav>
        </Navbar>
        <div className="container-fluid">
          <h2>Admin Panel</h2>
          {/* Add your admin panel content here */}
        </div>
      </div>
    </div>
  );
};

export default AdminPanel;
